import BarChart from "../components/Charts/BarChart";
import PieChart from "../components/Charts/PieChart";
import Grid from "@mui/material/Grid";

import { Translate } from "../utils/utils";
const DashboardPage = () => {
	return (
		<Grid container spacing={3}>
			<Grid item xs={12} md={8} lg={6}>
				<BarChart
					text={Translate("bar.title")}
					subtitle={`${Translate(
						"source"
					)} <a href="https://worldpopulationreview.com/world-cities" target="_blank">${Translate(
						"bar.worldLargestPopulation"
					)}</a>`}
					yAxisTitle={Translate("bar.PopulationMillion")}
					tooltip={`${Translate(
						"bar.Population2021"
					)}<b>{point.y:.1f} ${Translate("millions")}</b>`}
					DataName={Translate("Population")}
				></BarChart>
			</Grid>
			<Grid item xs={12} md={4} lg={6}>
				<PieChart
					subtitle={`${Translate(
						"source"
					)}<a href="https://www.mdpi.com/2072-6643/11/3/684/htm" target="_default">${Translate(
						"MDPI"
					)} </a>`}
					title={Translate("pie.title")}
				></PieChart>
			</Grid>
		</Grid>
	);
};

export default DashboardPage;
